import './App.css';
import React, { useState, useEffect } from 'react';
import RecallList from './components/RecallList';
import Dropdown from './components/Dropdown';
import { STAGES } from './helpers/constants';

const Main = () => {
    const [currentStage, setCurrentStage] = useState(STAGES.year);
    const [data, setData] = useState({ ...STAGES });
    const [inputStates, setInputStates] = useState({
        year: false,
        make: true,
        model: true,
    });

    useEffect(() => {
        console.log(data);
        if (
            currentStage.name === STAGES.year.name &&
            (!data.year || !data.year.dataset)
        ) {
            const getData = async () => {
                const set = await currentStage.getDataset();
                setData((d) => ({
                    ...d,
                    [currentStage.name]: {
                        ...currentStage,
                        dataset: set,
                    },
                }));
            };
            getData();
        }
    }, [currentStage, data.year]);

    const enableIfDisabled = (e) => {
        console.log(e.target.classList);
        if ([...e.target.classList].includes('disabled')) {
            setInputStates((current) => ({ ...current, year: false }));
        }
    };

    return (
        <div className="App">
            <h1>Vehicle Recall Finder</h1>
            <Dropdown stage={currentStage} />
            <RecallList
                make={'tesla'}
                data={data}
                model={'model y'}
                year={2020}
            />
        </div>
    );
};

export default Main;
