import './App.css';
import React, { useState, useEffect, useCallback } from 'react';

const STAGES = {
    year: 'Year',
    make: 'Make',
    model: 'Model',
};

// Avoid CORS errors by using a CORS proxy
const proxy = 'https://sheltered-temple-54799.herokuapp.com/';

const uppercaseFirst = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
};

const URLs = {
    modelYears: 'https://api.nhtsa.gov/products/vehicle/modelYears?issueType=r',
    makesForModelYear: (year) =>
        `https://api.nhtsa.gov/products/vehicle/makes?modelYear=${year}&issueType=r`,
    modelForMakeYear: (make, year) =>
        `https://api.nhtsa.gov/products/vehicle/models?modelYear=${year}&make=${make}&issueType=r`,
    recalls: (make, model, year) =>
        `https://api.nhtsa.gov/recalls/recallsByVehicle?make=${make}&model=${model}&modelYear=${year}`,
};

const RecallDetailCell = ({ recall }) => (
    <div className="detail-cell">
        <h2>
            {recall.NHTSACampaignNumber} / {recall.NHTSAActionNumber}
        </h2>
        <h3>Summary</h3>
        <p>{recall.Summary}</p>
        <h3>Consequence</h3>
        <p>{recall.Conequence}</p> {/* Lol yes, they did spell it wrong */}
    </div>
);

const RecallDetails = ({ make, model, year }) => {
    const [recalls, setRecalls] = useState([]);

    const fetchRecalls = useCallback(() => {
        fetch(proxy + URLs.recalls(make, model, year))
            .then((r) => r.json())
            .then((json) => {
                setRecalls(json.results);
            });
    }, [make, model, year]);

    useEffect(() => {
        if (!recalls.length) {
            fetchRecalls();
        }
    }, [recalls, fetchRecalls]);

    return (
        <div>
            <h1>{`Recalls for the ${year} ${uppercaseFirst(
                make
            )} ${uppercaseFirst(model)}`}</h1>
            {recalls.length &&
                recalls.map((recall) => (
                    <RecallDetailCell
                        recall={recall}
                        key={`r-${recall.NHTSACampaignNumber}`}
                    />
                ))}
        </div>
    );
};

const Main = () => {
    const [years, setYears] = useState({
        visible: [],
        all: [],
    });
    const [inputValue, setInputValue] = useState('');
    const [open, setOpen] = useState(false);
    const [currentStage, setCurrentStage] = useState(STAGES.year);
    const [inputStates, setInputStates] = useState({
        year: false,
        make: true,
        model: true,
    });

    const fetchYears = () => {
        fetch(proxy + URLs.modelYears)
            .then((r) => r.json())
            .then((data) => {
                const yrs = data.results.map((yearObj) => yearObj.modelYear);
                setYears({
                    all: yrs,
                    visible: yrs,
                });
            })
            .catch((r) => console.error(r));
    };

    const onChange = (e) => {
        setInputValue(e.target.value || '');
        if (!e.target.value) {
            setOpen(false);
            return;
        }
        setYears((yrs) => ({
            ...yrs,
            visible: yrs.all.filter((yr) => yr.includes(e.target.value)),
        }));
        setOpen(true);
    };

    useEffect(() => {
        if (!years || !years.all.length) {
            fetchYears();
        }
    }, [years]);

    const dropdownSelection = (value) => {
        setOpen(false);
        setInputValue(value);
        setInputStates((current) => ({ ...current, year: true }));
    };

    const enableIfDisabled = (e) => {
        console.log(e.target.classList);
        if ([...e.target.classList].includes('disabled')) {
            setInputStates((current) => ({ ...current, year: false }));
        }
    };

    const checkEnter = (e) => {
        if (e.key === 'Enter') {
            if (years.all.includes(e.target.value)) {
                // setSelectedValue
                setOpen(false);
                setInputStates((current) => ({ ...current, year: true }));
                e.target.blur();
            }
        }
    };

    return (
        <div className="App">
            <h1>Vehicle Recall Finder</h1>
            <input
                type="text"
                onClick={enableIfDisabled}
                className={inputStates.year && 'disabled'}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                onKeyDown={checkEnter}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
            <input
                type="text"
                onClick={enableIfDisabled}
                className={inputStates.year && 'disabled'}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                onKeyDown={checkEnter}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
            <input
                type="text"
                onClick={enableIfDisabled}
                className={inputStates.year && 'disabled'}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                onKeyDown={checkEnter}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
            <RecallDetails make={'tesla'} model={'model 3'} year={2020} />
        </div>
    );
};

export default Main;
