import './App.css';

const STAGES = {
    year: 'YEAR',
    make: 'MAKE',
    model: 'MODEL',
};

const URLs = {
    modelYears: 'https://api.nhtsa.gov/products/vehicle/modelYears?issueType=r',
    makesForModelYear: (year) =>
        `https://api.nhtsa.gov/products/vehicle/makes?modelYear=${year}&issueType=r`,
    modelForMakeYear: (make, year) =>
        `https://api.nhtsa.gov/products/vehicle/models?modelYear=${year}&make=${make}&issueType=r`,
    recalls: (make, model, year) =>
        `https://api.nhtsa.gov/recalls/recallsByVehicle?make=${make}&model=${model}&modelYear=${year}`,
};

const Main = () => {
    const fetchYears = () => {
        fetch(URLs.modelYears)
            .json()
            .then((data) => {
                console.log(data);
            });
    };

    const onChange = () => fetchYears();
    return (
        <div className="App">
            <h1>Vehicle Recall Finder</h1>
            <input
                type="text"
                placeholder="Enter a Make..."
                onChange={onChange}
            />
        </div>
    );
};

export default Main;
