import './App.css';
import React, { useState, useEffect } from 'react';

const STAGES = {
    year: 'Year',
    make: 'Make',
    model: 'Model',
};

// Avoid CORS errors by using a CORS proxy
const proxy = 'https://sheltered-temple-54799.herokuapp.com/';

const URLs = {
    modelYears: 'https://api.nhtsa.gov/products/vehicle/modelYears?issueType=r',
    makesForModelYear: (year) =>
        `https://api.nhtsa.gov/products/vehicle/makes?modelYear=${year}&issueType=r`,
    modelForMakeYear: (make, year) =>
        `https://api.nhtsa.gov/products/vehicle/models?modelYear=${year}&make=${make}&issueType=r`,
    recalls: (make, model, year) =>
        `https://api.nhtsa.gov/recalls/recallsByVehicle?make=${make}&model=${model}&modelYear=${year}`,
};

const Main = () => {
    const [years, setYears] = useState({
        visible: [],
        all: [],
    });
    const [inputValue, setInputValue] = useState('');
    const [open, setOpen] = useState(false);
    const [currentStage, setCurrentStage] = useState(STAGES.year);
    const [inputStates, setInputStates] = useState({
        ...STAGES,
    });

    const fetchYears = () => {
        fetch(proxy + URLs.modelYears)
            .then((r) => r.json())
            .then((data) => {
                const yrs = data.results.map((yearObj) => yearObj.modelYear);
                setYears({
                    all: yrs,
                    visible: yrs,
                });
            })
            .catch((r) => console.error(r));
    };

    const onChange = (e) => {
        setInputValue(e.target.value || '');
        if (!e.target.value) {
            setOpen(false);
            return;
        }
        setYears((yrs) => ({
            ...yrs,
            visible: yrs.all.filter((yr) => yr.includes(e.target.value)),
        }));
        setOpen(true);
    };

    useEffect(() => {
        if (!years || !years.all.length) {
            fetchYears();
        }
    }, [years]);

    const dropdownSelection = (value) => {
        setOpen(false);
        setInputValue(value);
    };

    const enableIfDisabled = (e) => {
        if (!e.target.disabled) {
            setInputStates((current) => ({ ...current, year: false }));
        }
    };

    return (
        <div className="App">
            <h1>Vehicle Recall Finder</h1>
            <input
                type="text"
                onClick={enableIfDisabled}
                disabled={inputStates.email}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Main;
