import './App.css';
import React, { useState, useEffect } from 'react';

const STAGES = {
    year: 'YEAR',
    make: 'MAKE',
    model: 'MODEL',
};

const proxy = 'https://sheltered-temple-54799.herokuapp.com/';

const URLs = {
    modelYears: 'https://api.nhtsa.gov/products/vehicle/modelYears?issueType=r',
    makesForModelYear: (year) =>
        `https://api.nhtsa.gov/products/vehicle/makes?modelYear=${year}&issueType=r`,
    modelForMakeYear: (make, year) =>
        `https://api.nhtsa.gov/products/vehicle/models?modelYear=${year}&make=${make}&issueType=r`,
    recalls: (make, model, year) =>
        `https://api.nhtsa.gov/recalls/recallsByVehicle?make=${make}&model=${model}&modelYear=${year}`,
};

const Main = () => {
    const [years, setYears] = useState({
        visible: [],
        all: [],
    });

    const fetchYears = () => {
        fetch(proxy + URLs.modelYears)
            .then((r) => r.json())
            .then((data) => {
                const yrs = data.results.map((yearObj) => yearObj.modelYear);
                setYears({
                    all: yrs,
                    visible: yrs,
                });
            })
            .catch((r) => console.error(r));
    };

    const onChange = (e) => {
        if (!e.target.value) return;
        setYears((yrs) => ({
            ...yrs,
            visible: yrs.all.filter((yr) => yr.includes(e.target.value)),
        }));
    };

    useEffect(() => {
        if (!years || !years.all.length) {
            fetchYears();
        }
    }, [years]);

    return (
        <div className="App">
            <h1>Vehicle Recall Finder</h1>
            <input
                type="text"
                placeholder="Enter a Make..."
                onChange={onChange}
            />
            <div class="dropdown">
                <button onclick="myFunction()" className="dropbtn">
                    Dropdown
                </button>
                <div id="myDropdown" className="dropdown-content">
                    <input
                        type="text"
                        placeholder="Search.."
                        id="myInput"
                        onkeyup="filterFunction()"
                    />
                    <a href="#about">About</a>
                    <a href="#base">Base</a>
                    <a href="#blog">Blog</a>
                    <a href="#contact">Contact</a>
                    <a href="#custom">Custom</a>
                    <a href="#support">Support</a>
                    <a href="#tools">Tools</a>
                </div>
            </div>
        </div>
    );
};

export default Main;
