import './App.css';
import React, { useState, useEffect } from 'react';
import RecallList from './components/RecallList';
import { URLs, STAGES } from './helpers/constants';

const Main = () => {
    const [years, setYears] = useState({
        visible: [],
        all: [],
    });
    const [inputValue, setInputValue] = useState('');
    const [open, setOpen] = useState(false);
    const [currentStage, setCurrentStage] = useState(STAGES.year);
    const [inputStates, setInputStates] = useState({
        year: false,
        make: true,
        model: true,
    });

    const fetchYears = () => {
        fetch(URLs.modelYears)
            .then((r) => r.json())
            .then((data) => {
                const yrs = data.results.map((yearObj) => yearObj.modelYear);
                setYears({
                    all: yrs,
                    visible: yrs,
                });
            })
            .catch((r) => console.error(r));
    };

    const onChange = (e) => {
        setInputValue(e.target.value || '');
        if (!e.target.value) {
            setOpen(false);
            return;
        }
        setYears((yrs) => ({
            ...yrs,
            visible: yrs.all.filter((yr) => yr.includes(e.target.value)),
        }));
        setOpen(true);
    };

    useEffect(() => {
        if (!years || !years.all.length) {
            fetchYears();
        }
    }, [years]);

    const dropdownSelection = (value) => {
        setOpen(false);
        setInputValue(value);
        setInputStates((current) => ({ ...current, year: true }));
    };

    const enableIfDisabled = (e) => {
        console.log(e.target.classList);
        if ([...e.target.classList].includes('disabled')) {
            setInputStates((current) => ({ ...current, year: false }));
        }
    };

    const checkEnter = (e) => {
        if (e.key === 'Enter') {
            if (years.all.includes(e.target.value)) {
                // setSelectedValue
                setOpen(false);
                setInputStates((current) => ({ ...current, year: true }));
                e.target.blur();
            }
        }
    };

    return (
        <div className="App">
            <h1>Vehicle Recall Finder</h1>
            <input
                type="text"
                onClick={enableIfDisabled}
                className={inputStates.year && 'disabled'}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                onKeyDown={checkEnter}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
            <input
                type="text"
                onClick={enableIfDisabled}
                className={inputStates.year && 'disabled'}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                onKeyDown={checkEnter}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
            <input
                type="text"
                onClick={enableIfDisabled}
                className={inputStates.year && 'disabled'}
                placeholder={`Enter a ${currentStage}...`}
                onChange={onChange}
                onKeyDown={checkEnter}
                value={inputValue}
            />
            <div className={`dropdown-values ${open ? 'show' : ''}`}>
                {years.visible.map((year) => (
                    <div
                        onClick={() => dropdownSelection(year)}
                        key={`y-${year}`}
                    >
                        {year}
                    </div>
                ))}
            </div>
            <RecallList make={'tesla'} model={'model y'} year={2020} />
        </div>
    );
};

export default Main;
